import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { z } from "zod";
import PptxGenJS from "pptxgenjs";

export async function registerRoutes(app: Express): Promise<Server> {
  const httpServer = createServer(app);

  app.post("/api/process-downtime", async (req, res) => {
    try {
      const { csvData } = req.body;

      if (!csvData || typeof csvData !== 'string') {
        return res.status(400).json({ error: "CSV data is required" });
      }

      const processedData = await storage.processDowntimeData(csvData);
      res.json(processedData);
    } catch (error) {
      console.error("Error processing downtime data:", error);
      res.status(500).json({ error: "Failed to process downtime data" });
    }
  });

  app.post("/api/weekly-reports", async (req, res) => {
    try {
      const { weekLabel, weekStart, weekEnd, csvData } = req.body;

      if (!weekLabel || typeof weekLabel !== 'string') {
        return res.status(400).json({ error: "Week label is required" });
      }

      if (!weekStart || !weekEnd) {
        return res.status(400).json({ error: "Week start and end dates are required" });
      }

      if (!csvData || typeof csvData !== 'string') {
        return res.status(400).json({ error: "CSV data is required" });
      }

      const processedData = await storage.processDowntimeData(csvData);
      const weeklyReport = await storage.saveWeeklyReport(weekLabel, weekStart, weekEnd, processedData);
      res.json(weeklyReport);
    } catch (error) {
      console.error("Error saving weekly report:", error);
      res.status(500).json({ error: "Failed to save weekly report" });
    }
  });

  app.get("/api/weekly-reports", async (req, res) => {
    try {
      const reports = await storage.getWeeklyReports();
      res.json(reports);
    } catch (error) {
      console.error("Error fetching weekly reports:", error);
      res.status(500).json({ error: "Failed to fetch weekly reports" });
    }
  });

  app.delete("/api/weekly-reports/:id", async (req, res) => {
    try {
      const { id } = req.params;
      await storage.deleteWeeklyReport(id);
      res.json({ success: true });
    } catch (error) {
      console.error("Error deleting weekly report:", error);
      res.status(500).json({ error: "Failed to delete weekly report" });
    }
  });

  app.post("/api/download-ppt", async (req, res) => {
    try {
      const { csvData } = req.body;

      if (!csvData || typeof csvData !== 'string') {
        return res.status(400).json({ error: "CSV data is required" });
      }

      const processedData = await storage.processDowntimeData(csvData);

      // Create PowerPoint presentation
      const pptx = new PptxGenJS();
      pptx.layout = 'LAYOUT_WIDE';

      // Title slide
      const titleSlide = pptx.addSlide();
      titleSlide.addText("Downtime Analysis Report", { 
        x: 1, y: 1, w: 8, h: 1.5, fontSize: 32, bold: true, color: "363636",
        align: "center"
      });
      titleSlide.addText(`Generated on ${new Date().toLocaleDateString()}`, { 
        x: 1, y: 2.5, w: 8, h: 0.5, fontSize: 16, color: "666666",
        align: "center"
      });

      // Summary slide
      const summarySlide = pptx.addSlide();
      summarySlide.addText("Overall Downtime Summary", { 
        x: 0.5, y: 0.5, w: 9, h: 1, fontSize: 24, bold: true, color: "363636"
      });

      // Calculate totals for summary table
      const serviceUptime = 10080 - processedData.summary.totalDuration;
      const unplannedPartialTotal = processedData.chartData.unplannedPartial.reduce((sum, item) => sum + item.duration, 0);
      const unplannedFullTotal = processedData.chartData.unplannedFull.reduce((sum, item) => sum + item.duration, 0);
      const plannedFullTotal = processedData.chartData.plannedFull.reduce((sum, item) => sum + item.duration, 0);
      const plannedPartialTotal = processedData.chartData.plannedPartial.reduce((sum, item) => sum + item.duration, 0);

      // Summary table
      const summaryTableData = [
        ["Category", "Duration (minutes)"],
        ["Service Uptime", serviceUptime.toLocaleString()],
        ["Unplanned Partial", unplannedPartialTotal.toString()],
        ["Unplanned Full", unplannedFullTotal.toString()],
        ["Planned Full", plannedFullTotal.toString()],
        ["Planned Partial", plannedPartialTotal.toString()]
      ];

      summarySlide.addTable(summaryTableData, {
        x: 1, y: 2, w: 6, h: 3,
        colW: [3, 3],
        border: { pt: 1, color: "CCCCCC" },
        fill: { color: "F7F7F7" },
        color: "363636",
        fontSize: 14,
        align: "center",
        valign: "middle"
      });

      // Key metrics
      const availability = (((serviceUptime) / (serviceUptime + processedData.summary.totalDuration)) * 100).toFixed(2);
      summarySlide.addText("Key Metrics", { 
        x: 1, y: 5.2, w: 6, h: 0.5, fontSize: 18, bold: true, color: "363636"
      });
      summarySlide.addText(`Total Downtime: ${Math.floor(processedData.summary.totalDuration / 60)}h ${processedData.summary.totalDuration % 60}m`, { 
        x: 1, y: 5.8, w: 6, h: 0.4, fontSize: 14, color: "666666"
      });
      summarySlide.addText(`Availability: ${availability}%`, { 
        x: 1, y: 6.3, w: 6, h: 0.4, fontSize: 14, color: "666666"
      });
      summarySlide.addText(`Planned vs Unplanned: ${processedData.summary.plannedDuration}min vs ${processedData.summary.unplannedDuration}min`, { 
        x: 1, y: 6.8, w: 6, h: 0.4, fontSize: 14, color: "666666"
      });

      // Incidents details slide
      const incidentsSlide = pptx.addSlide();
      incidentsSlide.addText("Incident Details", { 
        x: 0.5, y: 0.5, w: 9, h: 1, fontSize: 24, bold: true, color: "363636"
      });

      // Prepare incidents table data
      const incidentsTableData = [
        ["Date", "Issue", "Channel", "Impact", "Type", "Duration"]
      ];

      // Limit to first 15 incidents to fit on slide
      const limitedIncidents = processedData.incidents.slice(0, 15);
      limitedIncidents.forEach(incident => {
        incidentsTableData.push([
          incident.date,
          incident.issue.length > 30 ? incident.issue.substring(0, 30) + "..." : incident.issue,
          incident.channel,
          incident.impactType,
          incident.modality,
          incident.duration
        ]);
      });

      incidentsSlide.addTable(incidentsTableData, {
        x: 0.5, y: 1.5, w: 9, h: 5,
        colW: [1.2, 2.8, 1.5, 1, 1.2, 1.3],
        border: { pt: 1, color: "CCCCCC" },
        fill: { color: "F7F7F7" },
        color: "363636",
        fontSize: 10,
        align: "center",
        valign: "middle"
      });

      if (processedData.incidents.length > 15) {
        incidentsSlide.addText(`Showing first 15 of ${processedData.incidents.length} total incidents`, { 
          x: 0.5, y: 6.8, w: 9, h: 0.4, fontSize: 12, color: "666666",
          align: "center"
        });
      }

      // Generate PowerPoint file
      const pptxBuffer = await pptx.writeFile({ outputType: 'nodebuffer' });

      // Set response headers for file download
      res.setHeader('Content-Type', 'application/vnd.openxmlformats-officedocument.presentationml.presentation');
      res.setHeader('Content-Disposition', 'attachment; filename="downtime-analysis-report.pptx"');
      res.setHeader('Content-Length', pptxBuffer.length);

      res.send(pptxBuffer);
    } catch (error) {
      console.error("Error generating PowerPoint:", error);
      res.status(500).json({ error: "Failed to generate PowerPoint report" });
    }
  });

  return httpServer;
}

server/storage.ts.content:
import { type DowntimeIncident, type ProcessedData, type WeeklyReport } from "@shared/schema";

export interface IStorage {
  processDowntimeData(csvData: string): Promise<ProcessedData>;
  saveWeeklyReport(weekLabel: string, weekStart: string, weekEnd: string, data: ProcessedData): Promise<WeeklyReport>;
  getWeeklyReports(): Promise<WeeklyReport[]>;
  deleteWeeklyReport(id: string): Promise<void>;
}

export class MemStorage implements IStorage {
  private weeklyReports: WeeklyReport[] = [];
  async processDowntimeData(csvData: string): Promise<ProcessedData> {
    const lines = csvData.trim().split('\n');
    const incidents: DowntimeIncident[] = [];

    // Parse CSV data
    for (const line of lines) {
      const columns = line.split('\t');
      if (columns.length >= 10) {
        incidents.push({
          date: columns[0],
          issue: columns[1],
          channel: columns[2],
          service: columns[3],
          impactType: columns[4] as 'FULL' | 'PARTIAL',
          modality: columns[5] as 'PLANNED' | 'UNPLANNED',
          startTime: columns[7],
          endTime: columns[8],
          duration: columns[9],
          reason: columns[10] || 'Unspecified',
        });
      }
    }

    // Process and categorize data
    const chartData = this.categorizeIncidents(incidents);
    const summary = this.calculateSummary(incidents);

    return {
      summary,
      chartData,
      incidents,
    };
  }

  private categorizeIncidents(incidents: DowntimeIncident[]) {
    // Maps to store aggregated duration by channel for each category
    const plannedFullMap = new Map<string, number>();
    const unplannedFullMap = new Map<string, number>();
    const plannedPartialMap = new Map<string, number>();
    const unplannedPartialMap = new Map<string, number>();

    // Group by incident (same date + time + issue) to avoid double counting time periods
    const incidentGroups = new Map<string, {
      duration: number;
      channels: Set<string>;
      modality: string;
      impactType: string;
    }>();

    incidents.forEach(incident => {
      const key = `${incident.date}-${incident.startTime}-${incident.endTime}-${incident.issue}`;
      if (!incidentGroups.has(key)) {
        incidentGroups.set(key, {
          duration: this.parseDuration(incident.duration),
          channels: new Set(),
          modality: incident.modality,
          impactType: incident.impactType
        });
      }
      incidentGroups.get(key)!.channels.add(incident.channel);
    });

    // Process each unique incident group - for chart data we need per-channel breakdown
    incidentGroups.forEach(group => {
      const { duration, channels, modality, impactType } = group;

      // For chart visualization, we want to show duration per channel affected
      channels.forEach(channel => {
        if (modality === 'PLANNED' && impactType === 'FULL') {
          plannedFullMap.set(channel, (plannedFullMap.get(channel) || 0) + duration);
        } else if (modality === 'UNPLANNED' && impactType === 'FULL') {
          unplannedFullMap.set(channel, (unplannedFullMap.get(channel) || 0) + duration);
        } else if (modality === 'PLANNED' && impactType === 'PARTIAL') {
          plannedPartialMap.set(channel, (plannedPartialMap.get(channel) || 0) + duration);
        } else if (modality === 'UNPLANNED' && impactType === 'PARTIAL') {
          unplannedPartialMap.set(channel, (unplannedPartialMap.get(channel) || 0) + duration);
        }
      });
    });

    // Convert maps to arrays
    const plannedFull = Array.from(plannedFullMap.entries()).map(([channel, duration]) => ({ channel, duration }));
    const unplannedFull = Array.from(unplannedFullMap.entries()).map(([channel, duration]) => ({ channel, duration }));
    const plannedPartial = Array.from(plannedPartialMap.entries()).map(([channel, duration]) => ({ channel, duration }));
    const unplannedPartial = Array.from(unplannedPartialMap.entries()).map(([channel, duration]) => ({ channel, duration }));

    return {
      plannedFull,
      unplannedFull,
      plannedPartial,
      unplannedPartial,
    };
  }

  private calculateSummary(incidents: DowntimeIncident[]) {
    // Deduplicate incidents by same date + time for duration calculation
    const uniqueIncidents = new Map<string, DowntimeIncident>();

    incidents.forEach(incident => {
      const key = `${incident.date}-${incident.startTime}-${incident.endTime}-${incident.issue}`;
      if (!uniqueIncidents.has(key)) {
        uniqueIncidents.set(key, incident);
      }
    });

    const uniqueIncidentList = Array.from(uniqueIncidents.values());
    let totalDuration = 0;
    let plannedDuration = 0;
    let unplannedDuration = 0;

    uniqueIncidentList.forEach(incident => {
      const duration = this.parseDuration(incident.duration);
      totalDuration += duration;

      if (incident.modality === 'PLANNED') {
        plannedDuration += duration;
      } else {
        unplannedDuration += duration;
      }
    });

    return {
      totalIncidents: uniqueIncidentList.length,
      totalDuration,
      plannedDuration,
      unplannedDuration,
    };
  }

  private parseDuration(durationStr: string): number {
    // Parse duration string like "3:50:00" to minutes
    const parts = durationStr.split(':');
    const hours = parseInt(parts[0] || '0');
    const minutes = parseInt(parts[1] || '0');
    return hours * 60 + minutes;
  }

  async saveWeeklyReport(weekLabel: string, weekStart: string, weekEnd: string, data: ProcessedData): Promise<WeeklyReport> {
    const now = new Date();
    const id = `report_${Date.now()}`;

    const weeklyReport: WeeklyReport = {
      id,
      weekLabel,
      weekStart,
      weekEnd,
      createdAt: now.toISOString(),
      summary: data.summary,
    };

    this.weeklyReports.push(weeklyReport);
    return weeklyReport;
  }

  async getWeeklyReports(): Promise<WeeklyReport[]> {
    return [...this.weeklyReports].sort((a, b) => 
      new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
    );
  }

  async deleteWeeklyReport(id: string): Promise<void> {
    const index = this.weeklyReports.findIndex(report => report.id === id);
    if (index > -1) {
      this.weeklyReports.splice(index, 1);
    }
  }
}

export const storage = new MemStorage();

