import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { z } from "zod";

export const downtimeIncidentSchema = z.object({
  date: z.string(),
  issue: z.string(),
  channel: z.string(),
  service: z.string(),
  impactType: z.enum(['FULL', 'PARTIAL']),
  modality: z.enum(['PLANNED', 'UNPLANNED']),
  startTime: z.string(),
  endTime: z.string(),
  duration: z.string(),
  reason: z.string().optional(),
});

export const weeklyReportSchema = z.object({
  id: z.string(),
  weekLabel: z.string(),
  weekStart: z.string(),
  weekEnd: z.string(),
  createdAt: z.string(),
  summary: z.object({
    totalIncidents: z.number(),
    totalDuration: z.number(),
    plannedDuration: z.number(),
    unplannedDuration: z.number(),
  }),
});

export const processedDataSchema = z.object({
  summary: z.object({
    totalIncidents: z.number(),
    totalDuration: z.number(),
    plannedDuration: z.number(),
    unplannedDuration: z.number(),
  }),
  chartData: z.object({
    plannedFull: z.array(z.object({
      channel: z.string(),
      duration: z.number(),
    })),
    unplannedFull: z.array(z.object({
      channel: z.string(),
      duration: z.number(),
    })),
    plannedPartial: z.array(z.object({
      channel: z.string(),
      duration: z.number(),
    })),
    unplannedPartial: z.array(z.object({
      channel: z.string(),
      duration: z.number(),
    })),
  }),
  incidents: z.array(downtimeIncidentSchema),
});

export type DowntimeIncident = z.infer<typeof downtimeIncidentSchema>;
export type ProcessedData = z.infer<typeof processedDataSchema>;
export type WeeklyReport = z.infer<typeof weeklyReportSchema>;

