                    const percentage = ((value / data.reduce((a, b) => a + b, 0)) * 100).toFixed(1);
                    return `${label}: ${value} min (${percentage}%)`;
                  }
                }
              }
            }
          }
        });
      }
    }

    return () => {
      Object.values(chartsRef.current).forEach(chart => chart.destroy());
    };
  }, [chartData]);

  return (
    <div className="dashboard-section">
